import React, { Component} from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
//data
import data from '../data/data.json';
//componentes de la aplicacion
import Header from './Header';
import Navegacion from './Navegacion';
import Inicio from './Inicio';
import Preguntas from './Preguntas';
import Recetario from './Recetario';
import View from './View';
import Footer from './Footer';

class Router extends Component {

    state = {
        mostrar: [],
        recetas: [],
        view: [],
        spinner: false
    }

    componentWillMount() {
        this.setState({
            recetas: data,
            mostrar: data.varios
        });
    }
    componentDidMount() {
        const varios = [...this.state.recetas];
        let mostrar =  varios.filter( index => (
            index.categoria === "Recetas faciles"
        ) )
        this.setState({
            mostrar
        })
    }
    buscarRecetas = (categoria) => {
        const varios = [...this.state.recetas];
        let mostrar =  varios.filter( index => (
            index.categoria === categoria
        ) )
        this.setState({
            spinner: true
        })
        setTimeout(() => {
            this.setState({
                mostrar,
                spinner: false
            });
            
        }, 2000);
    }

    mostrarReceta = (receta) => {
        this.setState({
            view: receta
        });
    }

    render() {
        return (
            <BrowserRouter>
                <div className="header">
                    <div className="container">
                        <Navegacion />
                        <Header />
                    </div>
                </div>

                <div className="container">
                    <Switch>
                        <Route exact path="/" render={() => (

                            <Inicio />

                        )} />
                        <Route exact path="/preguntas" render={() => (

                            <Preguntas />

                        )} />

                        <Route exact path="/recetas" render={() => (

                            <Recetario 
                                categoria= {this.buscarRecetas}
                                recetas={this.state.mostrar}
                                mostrarReceta={this.mostrarReceta}
                                spinner={this.state.spinner}
                            />

                        )} />
                        <Route exact path="/recetas/:id" render={() => (

                            <View
                                view={this.state.view}
                            />

                        )} />
                    </Switch>
                </div>

                <Footer />
            </BrowserRouter>
        )
    }
}

export default Router;
