import React, { Component} from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
//data
import data from '../data/data.json';
//componentes de la aplicacion
import Header from './Header';
import Navegacion from './Navegacion';
import Inicio from './Inicio';
import Preguntas from './Preguntas';
import Recetario from './Recetario';
import Footer from './Footer';

class Router extends Component {

    state = {
        galletas: [],
        magdalenas: [],
        varios: [],
        pasteles: [],
        cremas: []
    }

    componentWillMount() {
        this.setState({
            galletas: data.galletas,
            magdalenas: data.magdalenas,
            varios: data.varios,
            pasteles: data.pasteles,
            cremas: data.cremas
        });
    }
    mostrarRecetas = (categoria) => {
        const varios = [...this.state.varios]
        // const res = varios.filter( (res) => {
        //     res.categoria === categoria
        // })
        console.log(varios);
    }
    render() {
        return (
            <BrowserRouter>
                <div className="header">
                    <div className="container">
                        <Navegacion />
                        <Header />
                    </div>
                </div>

                <div className="container">
                    <Switch>
                        <Route exact path="/" render={() => (

                            <Inicio />

                        )} />
                        <Route exact path="/preguntas" render={() => (

                            <Preguntas />

                        )} />

                        <Route exact path="/recetas" render={() => (

                            <Recetario 
                                categoria= {this.mostrarRecetas}
                            />

                        )} />
                    </Switch>
                </div>

                <Footer />
            </BrowserRouter>
        )
    }
}

export default Router;
