import React, {Component} from 'react';


class Preguntas extends Component {


    emailRef = React.createRef();
    preguntaRef = React.createRef();
    
    formularPregunta = (e) => {
        e.preventDefault();

        // const pregunta = {
        //     email: this.emailRef.current.value,
        //     pregunta: this.preguntaRef.current.value
        // }
    }

    render() {
        return(

            <div className="row justify-content-center">
                <div className="col-8 justify-content-center">

                    <from className="from" onSubmit={this.formularPregunta}> 
                        <div className="from-group">
                            <label className="">Email: </label>
                            <input type="email" ref={this.emailRef} className="from-control" value="Correo Electronico" />
                        </div>
                        <div className="from-group">
                            <label>Cual es tu Pregunta:</label>
                            <textarea ref={this.preguntaRef} className="from-control" value="Escribir..."></textarea>
                        </div>
                        <div className="from-group">
                            <button type="submit" className="btn btn-primary">Formular Pregunta</button>
                        </div>
                    </from>
                </div>
            </div>

        )
    }
}

export default Preguntas;
